import React from 'react'
import {Row, Col, Container} from 'react-bootstrap'

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {value: ''}

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    handleSubmit(event) {
        alert ('A name was submitted: ' + this.state.value);
        event.preventDefault();
    }

    handleChange2(event) {
        this.setState({value: event.target.value});
    }

    render() {
        return (
        <Container>
            <label>
                Your Input <br />
                Request to Server Response from Server<br />
            </label>
            <Row style={{justifyContent: "center"}}>
                <Col>
                    <form onSubmit={this.handleSubmit}>
                            <input type="name" value={this.state.value} onChange={this.handleChange} name="name" placeholder="Enter name"/>
                    </form>
                </Col>
                <Col>
                    <input type="ans" value={this.state.value} onChange={this.handleChange2} name="ans"/>
                </Col>
            </Row>
        </Container>


        );
    }
}

export default Home;
